
import Cookies from 'js-cookie'

var thisScript = document.currentScript;

var wrapper = document.createElement("div");
wrapper.setAttribute("id" , "behtarin_company_badge");
thisScript.before(wrapper);
var wrapper = document.getElementById("behtarin_company_badge");
var html = "<div style='position: relative;display: inline-block;background-image: url(data:image/svg+xml;base64,);background-position: 0 0;background-repeat: repeat-x;width: 125px; height: 25px; background-size: 25px;'><div style='position: absolute;height: 100%;background: url(data:image/svg+xml;base64,);background-repeat: repeat-x;background-size: 25px; width:{percent}%;' ></div></div>";

    
var coocki = Cookies.get("behtatin_star_cache");

if (coocki !== undefined) {
    html = html.replace("{percent}", coocki);
    wrapper.innerHTML = html;
} else {
    var xhr = new XMLHttpRequest()
    xhr.open("GET", "http://localhost/wp-json/business/v1/get-business?business_id=441")
    xhr.send()
    xhr.onload = function () {
        if (xhr.status === 200) {
            var response = JSON.parse(xhr.responseText);
            html = html.replace("{percent}", response);
            wrapper.innerHTML = html;
            Cookies.set("behtatin_star_cache", response, { expires: 1, });
        } else if (xhr.status === 404) {
            console.log("No records found")
        }
    }
}